// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword , signOut} from "firebase/auth";
import firebaseConfig from "../config/firebase.json"


// Initialize Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const connection   = require('../config/mysql.js');


var express = require('express');
var router = express.Router();



router.use(function(req, res, next) {
    next();
});

router.get('/', function(req, res) {
    res.send('hi1');
  });

router.post('/signup', function(req, res){
    var email = req.query.id;
    var password = req.query.password;
    var student_id = connection.escape(req.query.student_id);
    var school = connection.escape(req.query.school);
    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in
        const user = userCredential.user;
        connection.query(`INSERT INTO User \n VALUES ( "${user.uid}", ${student_id}, ${school});`)
        // ...
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
        console.log(errorMessage);
        console.log(errorCode)
        // ..
      });
    res.send('neejfmfjomdj')
});

router.post( '/signin', function(req, res){
  var email = req.query.id;
  var password = connection.escape(req.query.password);

  signInWithEmailAndPassword(auth, email, password)
  .then((userCredential) => {
    // Signed in
    const user = userCredential.user;
    console.log(user);
    res.send('veryfied');

    // ...
  })
  .catch((error) => {
    const errorCode = error.code;
    const errorMessage = error.message;
  });
}
)

router.post('/signout', function(req, res){
  signOut(auth).then(() => {
    // Sign-out successful.
  }).catch((error) => {
    // An error happened.
  });

})

module.exports = router;

