// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword , signOut} from "firebase/auth";
import firebaseConfig from "../config/firebase.json"
const cookieParser = require('cookie-parser');
import cookieConfig from "../config/cookie";




// Initialize Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const connection  = require('../config/mysql.js');



var express = require('express');
var router = express.Router();

router.use(cookieParser());
router.use(express.json());



router.use(function(req, res, next) {
    next();
});

router.get('/', function(req, res) {
    res.send('firebase_auth');
  });

router.post('/signup', async function(req, res){
    var request = req.body;
    var email = request.email;
    var password = request.password;
    var student_id = request.student_id;
    var school = request.school_id; // id로 인수 받음
    var grade = request.grade;
    var class_ = request.class;
    var nickname = request.nickname;
    var profile = request.profile_id;
    let idClass;
    idClass =  await getIdClass(school, grade, class_);
    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in
        const user = userCredential.user;
        const sql = 'INSERT INTO User(idClass,idStudent,nickname, idImage, idFirebase ) \n VALUES ( ?, ?, ?, ?, ?);'
        const para = [idClass, student_id, nickname, profile, user.uid]; // sql안의 변수
        connection.query(sql, para,
          (error, rows) => {
            if (error) {
              console.log(error);
              res.status(400).send(error.message);
            }
            res.sendStatus(200);
          } 
        )


        // store cookie
        // ...
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
        console.log(errorMessage);
        console.log(errorCode);
        res.status(400).send(errorCode);
        // ..
      });
    });

router.post('/signin', function(req, res){
  var request = req.body;
  var email = request.id;
  var password = request.password;
  var persist = request.persist;
  let idClass;
  let idUser
  signInWithEmailAndPassword(auth, email, password)
  .then((userCredential) => {
    // Signed in
    const user = userCredential.user;
    const sql = 'SELECT idUser, idClass FROM User WHERE idFirebase = ?'
    connection.query(sql, [user.uid], 
    (error, rows)=>{
      if (error){
        res.status(400).send(error.message);
    };
      console.log(rows);

      idClass = rows[0].idClass;
      idUser = rows[0].idUser;
      if (persist == true){
        res.cookie('idUser', idUser, {maxAge: 1000*60*60*24*7});
        res.cookie('idClass', idClass, {maxAge: 1000*60*60*24*7});// store cookie
      }
      else{
        res.cookie('idUser', idUser, cookieConfig);
        res.cookie('idClass', idClass, cookieConfig);// store cookie
      }

      res.send(200);
    })



    // ...
  })
  .catch((error) => {
    const errorCode = error.code;
    const errorMessage = error.message;
    console.log(errorCode)
    console.log(errorMessage);
    res.send(400);
  });
}
)

router.post('/signout', function(req, res){
  signOut(auth).then(() => {
    res.sendStatus(200);
    // Sign-out successful.
  }).catch((error) => {
    res.sendStatus(400);    // An error happened.
  });
})

async function getIdClass(schoolId, grade, class_){
  return new Promise((resolve, reject) => {
  const sql = 'select * from Class where idSchool = ? AND grade = ? AND class = ?'
  const para = [schoolId, grade, class_]
  connection.query(sql, [schoolId, grade, class_],
    function(error, rows){
      console.log( rows);
      if(error){
        console.log(error.message)
      }
      if(typeof rows == undefined){
        connection.query('INSERT INTO Class(idSchool, grade, class) VALUES(?, ?, ?) ', para, 
        (error, rows)=>{
          if(error){
            res.status(500).send(error.message);
          }
          console.log(rows[0].idClass);
          resolve(rows[0].idClass);

        }
        )
      }
      else{
        console.log(rows[0].idClass);
        resolve(rows[0].idClass);
      }
    }
    )
  }
  )
}

export {auth};
module.exports = router;

