            if (error) {
                logger.error('ERROR POST /api/image ' + error.name);
                return res.status(500).send(err.message);
              }
            logger.info('POST /api/image ');
            res.send(`${rows.insertId}`); // 파일 업로드 시 클라로 파일의 아이디 리턴
        })
      }
    );

})

